package sample;import java.sql.*;public class SQLConnect {    public static Connection getConnection() {        final String url = "jdbc:mysql://localhost:3306/dictionary";        final String user = "root";        final String password = "";        try {            Class.forName("com.mysql.jdbc.Driver");            System.out.println("Connected");            return DriverManager.getConnection(url, user, password);        } catch (ClassNotFoundException e) {            System.out.println("ERROR");            e.printStackTrace();        } catch (SQLException e) {            System.out.println("ERROR");            e.printStackTrace();        }        return null;    }}